import program from 'commander';

import login from './login';
import importTools from './import';

program
  .command('login')
  .description('Login to your Atlas account')
  .action(login);

program
  .command('import')
  .description('Import content to your Atlas')
  .option('-g, --gitbook', 'Import a gitbook (default)')
  .action(importTools);

program.parse(process.argv);
